import { useEffect, useState } from "react"
import { getUserById, updateUser } from "../../managers/UserManager"


export const UserEdit = () => {

    const [rareUser, setRareUser] = useState({})

    useEffect(() => {
        getUserById(userId)
            .then(setRareUser)
    }, [userId])

    const handleSubmit = (evt) => {
        evt.preventDefault()

        updateUser(userId)
    }

}
