import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { getUserById, updateUser } from "../../managers/UserManager"

export const UserEdit = () => {
    const navigate = useNavigate()
    const [editUser, setEditUser] = useState([])
    const { userId } = useParams()

    useEffect(() => {
        getUserById(userId).then(data => setEditUser(data))
    }, [userId])


    return (
        <>
            <section key={`user--${editUser.id}`}className="section">
                    <article className="panel is-info">
                        <h2 className="panel-heading">Change Access for User: {editUser?.user?.username}</h2>
                        <div className="panel-block">
                            <form style={{ width: "100%" }}>
                                <div className="field">
                                    <label htmlFor="is_staff" className="label">Access: </label>
                                    {
                                    editUser?.user?.is_staff 
                                    ?
                                    <button
                                        onClick={
                                            () => updateUser(userId)
                                            .then(() => navigate(`/users/${userId}`))
                                        }
                                    >Make Author</button>
                                    :
                                    <button
                                        onClick={
                                            () => updateUser(userId)
                                            .then(() => navigate(`/users/${userId}`))
                                        }
                                    >Make Admin</button>
                                    }           
                                </div>
                                {/* <div className="field">
                                    <label htmlFor="active" className="label">Access: </label>
                                    {
                                    editUser?.active 
                                    ?
                                    <button
                                        onClick={
                                            () => updateUser(userId)
                                            .then(() => navigate(`/users/${userId}`))
                                        }
                                    >Deactivate</button>
                                    :
                                    <button
                                        onClick={
                                            () => updateUser(userId)
                                            .then(() => navigate(`/users/${userId}`))
                                        }
                                    >ReActivate</button>
                                    }           
                                </div> */}
                            </form>
                        </div>
                    </article>
            </section>

            <button type="submit"
                onClick={() => {
                        navigate(`/users`)
                }} 
                className="button is-success">Back
            </button>
        </>
    )
}







