import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { getUserById, updateUser } from "../../managers/UserManager"

export const UserEdit = () => {
    const navigate = useNavigate()
    const [editUser, setEditUser] = useState([])
    const { userId } = useParams()

    useEffect(() => {
        getUserById(userId).then(data => setEditUser(data))
    }, [userId])

    const updateUserState = (property, value) => {
        const copy = { ...editUser }
        copy[property] = value
        setEditUser(copy)
    }

    return (
        <form>
            <section key={`user--${editUser.id}`}className="section">
                    <article className="panel is-info">
                        <h2 className="panel-heading">Update User</h2>
                        <div className="panel-block">
                            <form style={{ width: "100%" }}>
                                <div className="field">
                                    <label htmlFor="is_staff" className="label">Access: </label>
                                    <select type="select" name="is_staff" required autoFocus className="form-control"
                                        value={editUser?.user?.is_staff}
                                        onChange={
                                            (evt) => {
                                                updateUserState("is_staff", evt.target.value)
                                            }
                                        }>
                                            <option key={editUser?.user?.id} value={editUser?.user?.id}></option>
                                        </select>
                                </div>
                            </form>
                        </div>
                    </article>
            </section>

            <button type="submit"
                onClick={evt => {
                    evt.preventDefault()

                    const rareUser = {
                        id: editUser.id,
                        is_staff: editUser.user.is_staff
                    }

                    updateUser(rareUser)
                        .then(() => navigate(`/users/${userId}`))

                }} 
                className="button is-success">Save Edits
            </button>
        </form>
    )
}







