import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { getUserById } from "../../managers/UserManager"

export const updateUser = () => {
    const navigate = useNavigate()
    const [editUser, setEditUser] = useState([])
    const { userId } = useParams()

    useEffect(() => {
        getUserById(userId).then(data => setEditUser(data))
    }, [userId])

    const updateUserState = (property, value) => {
        const copy = { ...editUser }
        copy[property] = value
        setEditUser(copy)
    }

    return (
        <section key={`user--${editUser.id}`}className="section">
                <article className="panel is-info">
                    <h2 className="panel-heading">Update User</h2>
                    <div className="panel-block">
                        <form style={{ width: "100%" }}>
                            <div className="field">
                                <label htmlFor="is_staff" className="label">Access: </label>
                                <select type="select" name="is_staff" required autoFocus className="form-control"
                                    value={editUser?.user?.is_staff}
                                    onChange={
                                        (evt) => {
                                            updateUserState("is_staff", evt.target.value)
                                        }
                                    }></select>
                            </div>
                        </form>
                    </div>
                </article>
        </section>
    )
}







